import { useState, useEffect } from "react";

function Hello() {
  function byeFn() {
    console.log("bye T_T")
  }
  function hiFn() {
    console.log("Created !");
    return byeFn;
  }

  useEffect(hiFn,[]);
  return <h1>Hello</h1>
}

function App() {
  const [showing, setShowing] = useState(false);
  const onClick = () => setShowing(prev => !prev);

  return (
    <div>
      {showing ? <Hello /> : null}
      <button onClick={onClick}>{showing ? "hide" : "show"}</button>
    </div>
  )
}

export default App;
