import { useState, useEffect } from "react";

function Hello() {
  return <h1>Hello</h1>
}

function App() {
  const [showing, setShowing] = useState(false);
  const onClick = () => setShowing(prev => !prev);

  return (
    <div>
      {showing ? <Hello /> : null}
      <button onClick={onClick}>{showing ? "hide" : "show"}</button>
      //showing을 하면 hide라는 텍스트, 아니면 show라는 텍스트
    </div>
  )
}

export default App;
