import { useState, useEffect } from "react";

function App() {
  const [showing, setShowing] = useState(false);

  return (
    <div>
      <button>{showing ? "hide" : "show"}</button>
      //showing이면 hide라는 텍스트, 아니면 show라는 텍스트
    </div>
  )
}

export default App;
